import React, {Component} from 'react'

class App extends Component{

constructor(props){
  super(props)
  this.state = {
    items:[],
    loading:false
  }
}

componentDidMount(){

fetch("https://randomuser.me/api/?page=3&results=10")
.then((Response) => Response.json())
.then((Response) => {
  this.setState({
    items:Response.results,
    loading:true
  })
})

}

  render(){
    var {items,loading} = this.state

    if(!loading){
      return (
        <div>Loading ...</div>
      )
    }

    else{
    return (
      <div className="container">
        <table class="table table-hover">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">First</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td colspan="2">Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>
      </div>



    )
  }
}
}

export default App
